$(document).on('ready', function() {
  $(".slider-navigation nav ul").slick({
        infinite: true,
        autoplay: true,
        centerPadding: '120px',
        autoplaySpeed: 1200,
        slidesToShow: 6,
        slidesToScroll: 1,
        centerMode: true,
        focusOnSelect: true
  });    
  // $('.navigation .slick-slide[data-slick-index="-4"]').css('opacity','0.7');
  // $('.navigation .slick-slide[data-slick-index="3"]').css('opacity','0.7');
});

$(document).ready(function(){
   var mh = 0;
   $(".slider-navigation ul li").each(function () {
       var h_block = parseInt($(this).height());
       if(h_block > mh) {
          mh = h_block;
       };
   });
   $(".slider-navigation ul li").height(mh);
});

$('.costs-more').on('click', function(){
   $( ".costs-content" ).slideToggle( "slow" );
});