$(document).ready(function(){


   var mh = 0;
   $(".slider-navigation ul li").each(function () {
       var h_block = parseInt($(this).height());
       if(h_block > mh) {
          mh = h_block;
       };
   });
   $(".slider-navigation ul li").height(mh);

   $(".news .new-table .new").each(function () {
       var h_block = parseInt($(this).height());
       if(h_block > mh) {
          mh = h_block;
       };
   });
   $(".news .new-table .new").height(mh);


});

