// СКРИПТ ТАБОВ НАЧАЛО
// КОЛИЧЕСТВО ТАБОВ
var a = 0;
$(document).ready(function(){
  $( ".automation-tabs .tabs ul li" ).each(function( index ) {
    a++;
  });
  console.log( a );
});

// КНОПКА -
$('.automation .buttons .btn-prev').on('click', function(){
  var b = $('.automation .buttons').attr('data-index'); 

  if (b == 1) {    
    $(this).removeClass('active');
  }else{
    $('.automation .buttons .btn').addClass('active');
    b = b-1;
    $('.automation .buttons').attr('data-index', b);
  }

  $( ".tabs ul li a" ).removeClass('active');
  $( ".automation .tab-content .tab-content_tab" ).removeClass('active');
  $( ".tabs ul li a[data-index ="+b+"]" ).attr( "data-index", b ).addClass('active');
  $( ".automation .tab-content #tab-"+b ).addClass('active');

});

// КНОПКА +
$('.automation .buttons .btn-next').on('click', function(){
  var b = $('.automation .buttons').attr('data-index'); 
  

  if (b == a) {
    $(this).removeClass('active');
  }else{
    $('.automation .buttons .btn').addClass('active');
    b++;
    $('.automation .buttons').attr('data-index', b);
  }

  $( ".tabs ul li a" ).removeClass('active');
  $( ".automation .tab-content .tab-content_tab" ).removeClass('active');
  $( ".tabs ul li a[data-index ="+b+"]" ).attr( "data-index", b ).addClass('active');
  $( ".automation .tab-content #tab-"+b ).addClass('active');
  console.log(b);
});

// ТАБЫ СВЕРХУ
$('.automation-tabs .tabs ul li a').on('click', function(){
  $('.automation-tabs .tabs ul li a').removeClass('active');
  $(this).addClass('active');
  var b = $(this).attr('data-index');
  $('.automation .buttons').attr('data-index', b);
  if (b == a) {
    $('.automation .buttons .btn-prev').addClass('active');
    $('.automation .buttons .btn-next').removeClass('active');
  }else if(b == 1){
    $('.automation .buttons .btn-prev').removeClass('active');
    $('.automation .buttons .btn-next').addClass('active');
  }else{
    $('.automation .buttons a').addClass('active');
  }

  $( ".automation .tab-content .tab-content_tab" ).removeClass('active');
  $( ".automation .tab-content #tab-"+b ).addClass('active');
});
