$('.popup .close').on('click', function(){
    $('.popup').removeClass('popup-active');
    $(this).closest('.popup-window').removeClass('active');
    if($(this).closest('.popup-window').hasClass('my_video')){

     var my_video = $('.my_video iframe')[0].contentWindow;   
      my_video.postMessage('{"event": "command", "func": "pauseVideo", "args": ""}', "*");  

  }
});

$('.popup-open').on('click', function(){
  var b =  $(this).attr('data-popup');
  $('.popup').addClass('popup-active');
  $('.'+b).addClass('active');

  if(b == 'my_video'){

     var my_video = $('.my_video iframe')[0].contentWindow;   
      my_video.postMessage('{"event": "command", "func": "playVideo", "args": ""}', "*");   

  }  
      
});

$(document).mouseup(function (e) {
    var container = $('.popup-window');
    if (container.has(e.target).length === 0){
       $('.popup-window').removeClass('active');
       $('.popup').removeClass('popup-active');
       var my_video = $('.my_video iframe')[0].contentWindow;   
      my_video.postMessage('{"event": "command", "func": "pauseVideo", "args": ""}', "*");  
    }
});

